#!/usr/bin/env node

/**
 * Analysis Runner Helper - Semantic Graph Focused
 * Handles comprehensive project analysis using only the semantic graph
 */

const chalk = require('chalk');

async function runComprehensiveAnalysis({ projectPath, projectId, projectName }) {
  console.log(chalk.blue('üß† Running semantic graph analysis...'));
  
  let totalRecordsCreated = 0;
  let toolsInitialized = 0;
  let semanticGraphNodes = 0;
  
  try {
    // Try to load semantic graph service
    const { SemanticGraphService } = require('../../dist/services/semantic-graph');
    
    console.log(chalk.blue('üîó Initializing semantic graph service...'));
    const semanticGraph = new SemanticGraphService();
    await semanticGraph.initialize();
    
    console.log(chalk.blue('üìä Analyzing project structure and relationships...'));
    
    // TODO: Implement semantic graph population based on project analysis
    // This would include:
    // - File/class/method relationship mapping
    // - Import/dependency analysis  
    // - Business concept extraction
    // - Cross-reference building
    
    // For now, create basic project structure nodes
    const projectNodeId = await semanticGraph.addNode('BusinessConcept', {
      name: projectName,
      type: 'project',
      path: projectPath,
      project_id: projectId,
      description: `Project: ${projectName}`
    });
    
    totalRecordsCreated = 1;
    toolsInitialized = 1;
    semanticGraphNodes = 1;
    
    console.log(chalk.green(`‚úÖ Semantic graph analysis completed`));
    console.log(chalk.blue(`   - Project node created: ${projectNodeId}`));
    
    await semanticGraph.close();
    
    return {
      success: true,
      totalRecordsCreated,
      toolsInitialized,
      semanticGraphNodes,
      analysisType: 'semantic_graph_only'
    };
    
  } catch (error) {
    console.log(chalk.red(`‚ùå Semantic graph analysis failed: ${error.message}`));
    console.log(chalk.yellow('   Make sure to run: npm run build'));
    
    return {
      success: false,
      totalRecordsCreated: 0,
      toolsInitialized: 0,
      semanticGraphNodes: 0,
      error: error.message,
      analysisType: 'failed'
    };
  }
}

/**
 * Legacy basic analysis function - now redirects to semantic graph
 */
async function runBasicAnalysis(projectPath, projectId, projectName = 'Unknown') {
  console.log(chalk.yellow('‚ö†Ô∏è Redirecting basic analysis to semantic graph...'));
  
  const result = await runComprehensiveAnalysis({
    projectPath,
    projectId, 
    projectName
  });
  
  return {
    recordsCreated: result.totalRecordsCreated,
    filesScanned: result.semanticGraphNodes
  };
}

module.exports = {
  runComprehensiveAnalysis,
  runBasicAnalysis
};
